            </Backdrop>
            <p className="crypto-name">{props.coin}</p>
            <section className="crypto-in-USD-div">
                <p className="crypto-in-USD">{BTC} USD</p>
            </section>
            <section className="crypto-buttons-div">
                {
                    props.showSellButton ? <Button sx={{ backgroundColor: "red", color: "white", height: "80%"}}>Sell</Button> : null
                }
                <Button sx={{ backgroundColor: "green", color: "white", height: "80%"}}>Buy</Button>
            </section>
